text = ''
text2 = ""

text3=""

def bad_guy(bad_guy):
    ans=0
    for i in bad_guy:
        if i=='B':
            ans+=1
        elif i=='C':
            ans+=3
        elif i == 'D':
            ans+=5
    return ans


def p1(t):
    ans=0
    for i in t:
        ans+=bad_guy(i)
    print(ans)

def p2(t):
    p2_ans = 0
    for i in range(0,len(t),2):
        temp = t[i:i+2]
        if 'x' in temp:
            p2_ans+=bad_guy(temp)
        else:
            p2_ans+=2+bad_guy(temp)
    print(p2_ans)

def p3(t):        
    p3_ans = 0
    for i in range(0,len(t),3):
        temp = t[i:i+3]
        if temp.count('x') == 3:
            continue
        if temp.count('x')==2:
            p3_ans+=bad_guy(temp)
        elif temp.count('x') == 1:
            p3_ans+=2 + bad_guy(temp)
        else:
            p3_ans+=6+bad_guy(temp)   
    print(p3_ans)


def main():
    p1(text)
    p2(text2)
    p3(text3)

if __name__ == '__main__':
    main()